mapper.select:
select count(id)
from dish
where category_id = #{categoryId}

select dish.id, dish.name, category_id, price, image, description, dish.status, dish.update_time, category.name categoryName from dish, category
category_id = category.id and dish.name like concat('%', #{name}, '%') and dish.status = #{status} and category_id = #{categoryId}
order by dish.create_time desc
