mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser}
where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select count(id)
from setmeal
where category_id = #{categoryId}

select setmeal.id, category_id, setmeal.name, price, setmeal.status, description, image, setmeal.update_time, category.name categoryName from setmeal, category
category_id = category.id and name like concat('%', #{name}, '%') and category_id = #{categoryId} and setmeal.status = #{status}
order by setmeal.create_time desc

select dish.name, image, description, copies
from dish,
     setmeal_dish
where dish.id = dish_id
  and setmeal_id = #{id}



    name like concat('%', #{name}, '%')
    and category_id = #{categoryId}
    and setmeal.status = #{status}

order by create_time desc
